import React, {useEffect, useState} from "react";
import Service from "./Service";
import * as config from "./config"
import $ from ""

function ServicesSection() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [servicesConfig, setServiceConfig] = useState([]);

    /*
    useEffect(() => {
          
          config.servicesConfigLoader((error, result) => {
            if(error) {
                isLoaded(true);
                setError(error);
                console.error(error);
            }
            else {
                setIsLoaded(true);
                setServiceConfig(result.services);
            }
          });
      }, []); // Empty dependency array ensures that the effect runs only once

      Object.values(servicesConfig).forEach(service => {
        console.log(service);
      });
    */

      $.ajax({
        url: "http://localhost:3001/servicesConfig",
        method: "GET",
        success: function (data) {
            setServiceConfig(data.services);
        },
        error: function (xhr, status, error) {
            console.log(`Error: ${status} - ${error}`);
        }
    });

    if (error) {
        return <div>Error: {error.message}</div>
    }   
    else if (!isLoaded) {
        return <div>Loading...</div>
    }
    else {
        return (
            <div id="services-section">
                <h2>Services</h2>
                <div id="services-container">
                    { Object.values(servicesConfig).forEach(service => (
                        <Service 
                            serviceURL={`http://${service.ipAddress}:${service.port}`}
                            imgPath={service.logoPath}
                            altText={service.serviceName}
                            serviceName={service.serviceName}
                            ipAddress={service.ipAddress}
                            hostName={service.hostName}
                        />
                    ))}
                </div>
            </div>
        );
    }
}

export default ServicesSection;
