const dns = require('dns');

export function resolveIPV4Address(hostname) {
    let ipv4Address = "";
    dns.resolve4(hostname, (err, address) => {
        if (err) {
          console.error(`Error resolving host: ${err.message}`);
          return;
        }
        console.log(address.toString());
        ipv4Address = address.toString();
      });
}
