const dns = require('dns');

const options = {
    family: 4,
    
}

export function resolveIPV4Address(hostname) {
    let ipv4Address = "";
    dns.resolve4(hostname, options, (error, address) => {
        if (error) {
            console.error(`Error resolving host: ${error.message}`);
            return;
        }
        console.log(address);
        ipv4Address = address.toString();
    });
    return ipv4Address;
}
