import React from "react";
import Service from "./Service";
import * as config from "./config"

function ServicesSection() {

    const servicesConfig = config.servicesConfigLoader();
    useEffect(() => {
        // Function to be executed every second
        const updateSystemInfo = () => {
          
          systemConnector.getCpuUtilization((error, result) => {
            if(error) {
              console.error(error);
            }
            else {
              setCpuUtilizationPercent(result);
            }
          });
    
    
          systemConnector.getFreeDiskSpace((error, result) => {
            if(error) {
              console.error(error);
            }
            else {
              setFreeDiskSpace(result);
            }
          });
    
          systemConnector.getTotalDiskSpace((error, result) => {
            if(error) {
              console.error(error);
            }
            else {
              setTotalDiskSpace(result);
            }
          });
        };
    
        // Set up the interval
        const intervalId = setInterval(updateSystemInfo, 1000); // 1000 milliseconds = 1 second
    
        // Clean up the interval when the component unmounts
        return () => clearInterval(intervalId);
      }, []); // Empty dependency array ensures that the effect runs only once
    
    return (
        <div id="services-section">
            <h2>Services</h2>
            <div id="services-container">

                <Service 
                    serviceURL={"http://###.###.###.###:###"}
                    imgPath={"#"}
                    altText={"#"}
                    serviceName={"Proxmox VE"}
                    ipAddress={""}
                    hostName={"pve"}
                />
                <Service 
                    serviceURL={"http://nas-omv"}
                    imgPath={"#"}
                    altText={"#"}
                    serviceName={"Open Media Vault - NAS"}
                    ipAddress={""}
                    hostName={"nas-omv"}
                />
                <Service 
                    serviceURL={"http://###.###.###.###:###"}
                    imgPath={"#"}
                    altText={"#"}
                    serviceName={"Supermico IMPI"}
                    ipAddress={""}
                    hostName={"SMC-X8SIL-F"}
                />
            </div>
        </div>
    );
}

export default ServicesSection;
